import './App.css';
import Mensaje from './Mensaje';

const Description = () => {
  return <p>
    Esta es la app del curso Fullstack Bootcamp
  </p>
}

const App = () => { 
  return (
    <div className="App">
      <Mensaje color='red' message='Estamos trabajando'/>
      <Mensaje color='green' message='en un curso'/>
      <Mensaje color='yellow' message='de react'/>
      <Description />
    </div>
  );
}

export default App;
